import React, { useState } from 'react';
import Topics from './Topics';
import Programs from './Programs';
import openDayData from './data/OpenDay.json';
import './App.css'; // for any app-specific styles

function App() {
  const [selectedTopic, setSelectedTopic] = useState(null);

  const handleSelectTopic = (topic) => {
    setSelectedTopic(topic);
  };

  const handleBackToTopics = () => {
    setSelectedTopic(null);
  };

  return (
    <div className="App">
      <header>
        <h1>Open Day</h1>
      </header>
      <main>
        {selectedTopic === null ? (
          <Topics topics={openDayData.topics} onSelectTopic={handleSelectTopic} />
        ) : (
          <Programs programs={selectedTopic.programs} onBack={handleBackToTopics} />
        )}
      </main>
    </div>
  );
}

export default App;
