// src/components/Programs.js
import React, { useState } from 'react';
import Map from './Map';
import './App.css';

const Programs = ({ programs, onBack }) => {
  const [selectedLocation, setSelectedLocation] = useState(null);

  // Handler when a program is clicked – opens the map modal using its location data
  const handleProgramClick = (program) => {
    if (program.location && program.location.latitude && program.location.longitude) {
      setSelectedLocation(program.location);
    }
  };

  return (
    <div>
      <button onClick={onBack} className="back-button">Back to Topics</button>
      <div className="programs-container">
        {programs.map(program => (
          <div 
            className="program-card" 
            key={program.id} 
            onClick={() => handleProgramClick(program)}
          >
            {program.cover_image && (
              <img src={program.cover_image} alt={program.title} className="program-image" />
            )}
            <h3>{program.title}</h3>
            <p>{program.description}</p>
            <p><strong>Time:</strong> {program.start_time} - {program.end_time}</p>
            <p><strong>Location:</strong> {program.room}</p>
          </div>
        ))}
      </div>

      {selectedLocation && (
        <Map 
          location={selectedLocation} 
          onClose={() => setSelectedLocation(null)} 
        />
      )}
    </div>
  );
};

export default Programs;
