// src/App.js
import React, { useState } from 'react';
import Topics from './Topics';
import Programs from './Programs';
import openDayData from './OpenDay.json';
import './App.css'; // for any app-specific styles

// Helper function to extract date and time from an ISO datetime string.
const getDateAndTime = (datetime) => {
  const [date, fullTime] = datetime.split('T');
  const time = fullTime.slice(0, 5); // "HH:MM"
  return { date, time };
};

function App() {
  const [selectedTopic, setSelectedTopic] = useState(null);

  const handleSelectTopic = (topic) => {
    setSelectedTopic(topic);
  };

  const handleBackToTopics = () => {
    setSelectedTopic(null);
  };

  const { cover_image, description, start_time, end_time } = openDayData;
  const { date: eventDate, time: startTime } = getDateAndTime(start_time);
  const { time: endTime } = getDateAndTime(end_time);

  return (
     <div className="app-container">
      <div className="hero-image" style={{ backgroundImage: `url(${cover_image})` }}>
        <div className="overlay-card">
          <h1>{description}</h1>
          <p><strong>Date:</strong> {eventDate}</p>
          <p><strong>Time:</strong> {startTime} - {endTime}</p>
      <main>
        {selectedTopic === null ? (
          <Topics topics={openDayData.topics} onSelectTopic={handleSelectTopic} />
        ) : (
          <Programs programs={selectedTopic.programs} onBack={handleBackToTopics} />
        )}
      </main>
      </div>
      </div>
    </div>
  );
}

export default App;
