// src/components/Programs.js
import React, { useState } from 'react';
import MapModal from './Map';
import './App.css';

const Programs = ({ programs, onBack }) => {
  const [selectedLocation, setSelectedLocation] = useState(null);

  const handleProgramClick = (program) => {
    if (program.location && program.location.latitude && program.location.longitude) {
      setSelectedLocation(program.location);
    }
  };

  // Helper function to split the datetime ("YYYY-MM-DDTHH:MM:SS") into
  // { date: "YYYY-MM-DD", time: "HH:MM" } 
  const getDateAndTime = (datetime) => {
    const [date, fullTime] = datetime.split('T');
    const time = fullTime.slice(0, 5); // take first 5 chars => "HH:MM"
    return { date, time };
  };

  return (
    <div>
      <button onClick={onBack} className="back-button">Back to Topics</button>

      <div className="programs-container">
        {programs.map((program) => {
          // Extract date/time for start and end
          const { date: startDate, time: startTime } = getDateAndTime(program.start_time);
          const { date: endDate, time: endTime } = getDateAndTime(program.end_time);

          return (
            <div
              className="program-card"
              key={program.id}
              onClick={() => handleProgramClick(program)}
            >
              <div className="program-text">
                <h3>{program.title}</h3>
                <p>{program.description}</p>
              </div>
              <div className="program-info">
                {/* If the start date and end date differ, you could choose how to display them.
                    Here we'll assume it's the same date, but you can adapt as needed. */}
                <p><strong>Date:</strong> {startDate}</p>
                <p>
                  <strong>Time:</strong> {startTime} - {endTime}
                </p>
                <p><strong>Room:</strong> {program.room}</p>
              </div>
            </div>
          );
        })}
      </div>

      {selectedLocation && (
        <MapModal
          location={selectedLocation}
          onClose={() => setSelectedLocation(null)}
        />
      )}
    </div>
  );
};

export default Programs;
